mapper.select:
SELECT * FROM SYS_ORGAN WHERE 1=1
AND organ_type= #{organType,jdbcType=VARCHAR}
AND DELETED = '0' AND tier_code LIKE #{tierCode,jdbcType=VARCHAR}
AND organ_name like #{organName,jdbcType=VARCHAR}
ORDER BY tier_code

select ID
from SYS_ORGAN
where ORGAN_NAME = #{name,jdbcType=VARCHAR}
and PARENT_ID = #{parentId,jdbcType=VARCHAR}
and DELETED = '0'

select ID
from SYS_ORGAN
where ORGAN_NAME = #{name,jdbcType=VARCHAR}
and PARENT_ID = #{parentId,jdbcType=VARCHAR}
and ID != #{id,jdbcType=VARCHAR}
and DELETED = '0'

select ID
from SYS_ORGAN
where ORGAN_CODE = #{organCode,jdbcType=VARCHAR}

	and ID != #{id,jdbcType=VARCHAR}

and DELETED = '0'
