mapper.select:
SELECT JOB_TYPE_ID, JOB_TYPE_NAME, DISCRIPTION, JOB_CLASS, STATE, CREATE_TIME, LAST_UP_TIME, USER_ID, SORT_NO FROM SYS_JOB_TYPE
WHERE STATE=#{state,jdbcType=INTEGER}
ORDER BY SORT_NO

SELECT JOB_TYPE_ID, JOB_TYPE_NAME, DISCRIPTION, JOB_CLASS, STATE, CREATE_TIME, LAST_UP_TIME, USER_ID, SORT_NO FROM SYS_JOB_TYPE
WHERE JOB_TYPE_ID=#{jobTypeId,jdbcType=VARCHAR}
ORDER BY SORT_NO
