mapper.select:
select * from SYS_USER where DELETED = '0'

	and ID = #{id,jdbcType=VARCHAR}


	and LOGIN_NAME like
	#{loginName,jdbcType=VARCHAR}


	and FULLNAME like
	#{fullname,jdbcType=OTHER}


	and SEX = #{sex,jdbcType=VARCHAR}


	and BIRTHDAY =
	#{birthday,jdbcType=VARCHAR}


	and ORGAN_ID =
	#{organId,jdbcType=VARCHAR}


	and POSITION =
	#{position,jdbcType=VARCHAR}


	and POST = #{post,jdbcType=VARCHAR}


	and TEL = #{tel,jdbcType=VARCHAR}


	and MOBILE = #{mobile,jdbcType=VARCHAR}


	and EMAIL = #{email,jdbcType=VARCHAR}


	and ENABLE = #{enable,jdbcType=VARCHAR}

order by CREATE_TIME desc

select count(*) from SYS_USER where DELETED = '0'
and ID = #{id,jdbcType=VARCHAR}
and LOGIN_NAME like #{loginName,jdbcType=VARCHAR}
and FULLNAME like #{fullname,jdbcType=OTHER}
and SEX = #{sex,jdbcType=VARCHAR},
and BIRTHDAY = #{birthday,jdbcType=VARCHAR}
and ORGAN_ID = #{organId,jdbcType=VARCHAR},
and POSITION = #{position,jdbcType=VARCHAR},
and POST = #{post,jdbcType=VARCHAR},
and TEL = #{tel,jdbcType=VARCHAR},
and MOBILE = #{mobile,jdbcType=VARCHAR},
and EMAIL = #{email,jdbcType=VARCHAR},
and ENABLE = #{enable,jdbcType=VARCHAR},

select distinct u.* from SYS_USER u left join SYS_PART_TIME pt on u.id=pt.USER_ID
DELETED = '0'
AND (u.ORGAN_ID = #{organId,jdbcType=VARCHAR} or pt.ORGAN_ID = #{organId,jdbcType=VARCHAR} )
AND u.FULLNAME like #{name,jdbcType=VARCHAR}
and u.MOBILE like #{mobile,jdbcType=VARCHAR}

select count(distinct(u.id)) from SYS_USER u left join SYS_PART_TIME pt on u.id=pt.USER_ID
DELETED = '0'
AND (u.ORGAN_ID = #{organId,jdbcType=VARCHAR} or pt.ORGAN_ID = #{organId,jdbcType=VARCHAR} )
AND u.FULLNAME like #{name,jdbcType=VARCHAR}
and u.MOBILE like #{mobile,jdbcType=VARCHAR}

select distinct t.* from SYS_USER t
left join sys_organ a on t.organ_id = a.id
where t.deleted='0' and a.deleted='0'
and a.tier_code like
#{tierCode,jdbcType=VARCHAR}

	AND t.FULLNAME like #{name,jdbcType=VARCHAR}


	and t.MOBILE like
	#{mobile,jdbcType=VARCHAR}

select count(distinct(t.id)) from SYS_USER t
left join sys_organ a on t.organ_id = a.id
where t.deleted='0' and a.deleted='0'
and a.tier_code like
#{tierCode,jdbcType=VARCHAR}

	AND t.FULLNAME like #{name,jdbcType=VARCHAR}


	and t.MOBILE like
	#{mobile,jdbcType=VARCHAR}

select * from SYS_USER where DELETED = '0' and ORGAN_ID =
#{organId,jdbcType=VARCHAR}
union
select u.* from SYS_PART_TIME partTime join SYS_USER u on u.id =
partTime.USER_ID
where u.DELETED = '0' and partTime.ORGAN_ID = #{organId,jdbcType=VARCHAR}

select ROLE_ID as userId from sys_user_role_part_time
where user_id = #{userId,jdbcType=VARCHAR} and organ_id =
#{organId,jdbcType=VARCHAR}
